import { ApplicationRef, ComponentFactoryResolver, Injectable, Injector, ViewContainerRef } from '@angular/core';
import { ToastComponent } from '../../shared/components/toast/toast.component';
import { ToastInterface } from './toastInterface';

@Injectable({
  providedIn: 'root'
})
export class ToastService {

  constructor(
    private appRef: ApplicationRef,
    private injector: Injector,
    private componentFactoryResolver: ComponentFactoryResolver,
    private viewContainerRef: ViewContainerRef 
  ) {}

  public show(toastOptions : ToastInterface) {
    const componentRef = this.viewContainerRef.createComponent(ToastComponent);
    componentRef.instance.mensajeInput = toastOptions.message;
    componentRef.instance.text = toastOptions.text;
    componentRef.instance.linkText = toastOptions.linkText;
    componentRef.instance.type = this.calculateType(toastOptions.possition, toastOptions.type);
    // const componentFactory = this.componentFactoryResolver.resolveComponentFactory(ToastComponent);
    // const componentRef = componentFactory.create(this.injector);
    // componentRef.instance.message = message;
    // this.appRef.attachView(componentRef.hostView);
    // const domElement = (componentRef.hostView as any).rootNodes[0] as HTMLElement;
    // document.body.appendChild(domElement);

  }

  private calculateType(position: string | undefined, type: string | undefined): { [key: string]: boolean } {
    const styles: { [key: string]: boolean } = {};
  
    if (type === 'error') {
      styles['bg-danger'] = true; // Utilizar la clase CSS directamente
    } else {
      styles['bg-light'] = true; // Clase de fondo por defecto
    }
  
    switch (position) {
      case 'top-right':
        styles['top-0'] = true;
        styles['end-0'] = true;
        break;
      case 'bottom-right':
        styles['bottom-0'] = true;
        styles['end-0'] = true;
        break;
      default:
        styles['bottom-0'] = true;
        styles['start-50'] = true;
        styles['translate-middle-x'] = true;
        break;
    }
  
    return styles;
  }


}

import { Injectable } from '@angular/core';
import {  catchError, map, Observable, of, throwError } from 'rxjs';
import { ResponseMail } from '../interfaces/response-mail';
import { HttpClient, HttpErrorResponse, HttpParams } from '@angular/common/http';
import { environment } from '../../../../../enviorments/enviorment';
import { Login } from '../interfaces/login';
import { Authorize } from '../interfaces/authorize';
@Injectable({
  providedIn: 'root'
})
export class UserService {


  constructor(private httpClient: HttpClient) { }

  public checkMail(email : string) : Observable<ResponseMail> {
    const queryParams = new HttpParams().append('email', email);
    return this.httpClient
    .get<ResponseMail>(`${environment.baseUrl}/api/login/login`, {
      params : queryParams
    });
  }

  public register(registro : ResponseMail) : Observable<ResponseMail>{
    return this.httpClient.post<ResponseMail>(`${environment.baseUrl}/api/login/create`, registro)
    .pipe(catchError(error =>  {
      return of(error.error as ResponseMail);
    }));
  }

  public confirm(id : string) : Observable<boolean>{
    return this.httpClient.get<ResponseMail>(`${environment.baseUrl}/api/login/login/confirm/${id}`)
    .pipe(map(() => true),
    catchError(() =>  throwError(false))
    );
  }

  public sendReset(value: string) {
    return this.httpClient.post<ResponseMail>(`${environment.baseUrl}/api/login/reset`, value)
    .pipe(catchError(error =>  {
      return of(error.error as ResponseMail);
    }));  
  }

  login(login: Login) : Observable<Authorize> {
    return this.httpClient.post<Authorize>(`${environment.baseUrl}/api/login/login`, login);
  }

}

